import React, { useState, useEffect } from "react";
import objectArray from "../Functions/objectArray";

const IngredientDropdown = () => {
  const [ingredients, setIngredients] = useState([]);
  const [selectedIngredient, setSelectedIngredient] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      const data = await objectArray();
      setIngredients(data);
    };

    fetchData();
  }, []);

  const handleSelectChange = (event) => {
    setSelectedIngredient(event.target.value);
  };

  return (
    <div>
      <select value={selectedIngredient} onChange={handleSelectChange}>
        <option value="">Select an ingredient</option>
        {ingredients.map((ingredient) => (
          <option key={ingredient.id} value={ingredient.name}>
            {ingredient.name} console.log(ingredient)
          </option>
        ))}
      </select>
      {selectedIngredient && <h1>{selectedIngredient}</h1>}
    </div>
  );
};

export default IngredientDropdown;
