import styled from "styled-components";
import { useState, useEffect } from "react";
import React from "react";
import { newDataArray } from "../Functions/objectArray";

const SearchBar = ({ userIngredients, setUserIngredients }) => {
  const [inputValue, setInputValue] = useState("");
  const [ingredients, setIngredients] = useState([]);

  const [selectedIds, setSelectedIds] = useState([]);

  const handleClear = () => {
    setInputValue("");
  };

  const filteredSuggestions = ingredients.filter((ingredient) => {
    const ingredientLowerCase = ingredient.name.toLowerCase();
    const userInputLowerCase = inputValue.toLowerCase();

    return (
      ingredientLowerCase.includes(userInputLowerCase) &&
      userInputLowerCase.length > 1
    );
  });

  const handleClick = (name, id) => {
    setUserIngredients([...userIngredients, { name, id }]);
    setSelectedIds([...selectedIds, id]);

    const addItemToCard = async () => {
      try {
        const response = await fetch("/api/itemInCart", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            itemId: data._id.toString(),
            name: data.name,
            price: data.price,
            body_location: data.body_location,
            category: data.category,
            companyId: data.companyId.toString(),
            imageSrc: data.imageSrc,
            itemAmount: 1,
          }),
        });
        if (response.status === 201) {
          console.log("Item was added to cart");
          setUpdatedNumInStock(data.numInStock - 1);
          alert("Item was added to the cart");
        } else {
          console.log("Item is out of stock");
          alert("Sorry! That item is out of stock");
        }
      } catch (error) {
        console.log("There was an error:", error);
      }
    };
  };
  useEffect(() => {
    const fetchData = async () => {
      setIngredients(newDataArray);
    };

    fetchData();
  }, []);

  return (
    <React.Fragment>
      <InputWrapper>
        <Input
          type="text"
          value={inputValue}
          onChange={(ev) => setInputValue(ev.target.value)}
        />
        <Button onClick={handleClear}>Clear</Button>
      </InputWrapper>
      {inputValue.length >= 1 && filteredSuggestions.length > 0 && (
        <Suggestions>
          <ul>
            {filteredSuggestions.map((ingredient) => {
              const isDisabled = selectedIds.includes(ingredient.id);
              const className = isDisabled ? "disabled" : "";
              return (
                <SuggestionItem
                  key={ingredient.id}
                  value={ingredient.name}
                  onClick={() => handleClick(ingredient.name, ingredient.id)}
                  className={className}
                >
                  {ingredient.name}
                </SuggestionItem>
              );
            })}
          </ul>
        </Suggestions>
      )}
      <UL>
        {userIngredients.map((ingredient) => (
          <li key={ingredient.id}>
            {ingredient.name}({ingredient.id})
          </li>
        ))}
      </UL>
    </React.Fragment>
  );
};

const UL = styled.ul`
  margin-left: 700px;
`;

const InputWrapper = styled.div`
  display: flex;
  align-items: center;
  flex-direction: row;
`;
const Suggestions = styled.div`
  width: 25em;
  margin-top: 0.5em;
  padding: 0;
  box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.2);
  background-color: white;
  border: 1px solid lightgray;
  border-radius: 3px;
  z-index: 1;
`;

const SuggestionItem = styled.div`
  padding: 0.4em;
  cursor: pointer;
  &:hover {
    background-color: lightgoldenrodyellow;
  }

  &.disabled {
    color: gray;
    cursor: default;
    pointer-events: none;
  }
`;

const Input = styled.input`
  padding: 0.4em;
  width: 25em;
  border: 1px solid lightgray;
  border-radius: 3px;
`;

const Button = styled.button`
  padding: 0.5em;
  width: 5em;
  margin-left: 0.7em;
  color: white;
  background-color: rgb(0, 0, 200);
  border-radius: 5px;
  border: none;
`;

export default SearchBar;
