import React, { useState } from "react";
import objectArray from "../Functions/objectArray";

const IngredientSearchBar = () => {
  const [input, setInput] = useState("");
  const [ingredients, setIngredients] = useState(objectArray);

  const handleChange = (e) => {
    setInput(e.target.value);
  };

  const filteredIngredients = ingredients.filter((ingredient) =>
    ingredient.name.toLowerCase().includes(input.toLowerCase())
  );

  return (
    <div>
      <input type="text" onInput={handleChange} />
      <ul>
        {filteredIngredients.map((ingredient) => (
          <li key={ingredient.id}>{ingredient.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default IngredientSearchBar;
