import styled from "styled-components";
import { useState, useEffect } from "react";
import React from "react";
import { newDataArray } from "../Functions/objectArray";

const SearchBar = () => {
  const [inputValue, setInputValue] = useState("");
  const [ingredients, setIngredients] = useState([]);
  const [userIngredients, setUserIngredients] = useState([]);
  const handleClear = () => {
    setInputValue("");
  };

  const filteredSuggestions = ingredients.filter((ingredient) => {
    const ingredientLowerCase = ingredient.name.toLowerCase();
    const userInputLowerCase = inputValue.toLowerCase();

    return (
      ingredientLowerCase.includes(userInputLowerCase) &&
      userInputLowerCase.length > 1
    );
  });

  const handleClick = (name, id) => {
    setUserIngredients([...userIngredients, { [id]: name }]);
    console.log(userIngredients);
  };
  useEffect(() => {
    const fetchData = async () => {
      setIngredients(newDataArray);
    };

    fetchData();
  }, []);

  return (
    <React.Fragment>
      <InputWrapper>
        <Input
          type="text"
          value={inputValue}
          onChange={(ev) => setInputValue(ev.target.value)}
        />
        <Button onClick={handleClear}>Clear</Button>
      </InputWrapper>
      {inputValue.length >= 1 && filteredSuggestions.length > 0 && (
        <Suggestions>
          <ul>
            {filteredSuggestions.map((ingredient) => {
              return (
                <SuggestionItem
                  key={ingredient.id}
                  value={ingredient.name}
                  onClick={() => handleClick(ingredient.name, ingredient.id)}
                >
                  {ingredient.name}
                </SuggestionItem>
              );
            })}
          </ul>
        </Suggestions>
      )}
      <UL>
        {userIngredients.map((ingredient) => (
          <li key={Object.values(ingredient)[0]}>
            {Object.keys(ingredient)[0]}
          </li>
        ))}
      </UL>
    </React.Fragment>
  );
};

const UL = styled.ul`
  margin-left: 7 00px;
`;

const InputWrapper = styled.div`
  display: flex;
  align-items: center;
  flex-direction: row;
`;
const Suggestions = styled.div`
  width: 25em;
  margin-top: 0.5em;
  padding: 0;
  box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.2);
  background-color: white;
  border: 1px solid lightgray;
  border-radius: 3px;
  z-index: 1;
`;

const SuggestionItem = styled.div`
  padding: 0.4em;
  cursor: pointer;
  &:hover {
    background-color: lightgoldenrodyellow;
  }
`;

const Input = styled.input`
  padding: 0.4em;
  width: 25em;
  border: 1px solid lightgray;
  border-radius: 3px;
`;

const Button = styled.button`
  padding: 0.5em;
  width: 5em;
  margin-left: 0.7em;
  color: white;
  background-color: rgb(0, 0, 200);
  border-radius: 5px;
  border: none;
`;

export default SearchBar;
