const Typeahead = ({ suggestions, handleSelect, categories }) => {
  const [inputValue, setInputValue] = useState("");

  // const handleInputChange = (ev) => {
  //   setInputValue(ev.target.value);
  // };

  const handleClear = () => {
    setInputValue("");
  };

  // const handleKeyDown = (ev) => {
  //   if ((ev.key = "Enter")) {
  //     handleSelect(ev.target.inputValue);
  //   }
  // };

  const filteredSuggestions = suggestions.filter((suggestion) => {
    const suggestionLowerCase = suggestion.title.toLowerCase();
    const userInputLowerCase = inputValue.toLowerCase();

    return (
      suggestionLowerCase.includes(userInputLowerCase) &&
      userInputLowerCase.length > 1
    );
  });

  return (
    <React.Fragment>
      <InputWrapper>
        <Input
          type="text"
          value={inputValue}
          onChange={(ev) => setInputValue(ev.target.value)}
          onKeyDown={(ev) => {
            if (ev.key === "Enter") {
              handleSelect(ev.target.value);
            }
          }}
        />
        <Button onClick={handleClear}>Clear</Button>
      </InputWrapper>
      {inputValue.length > 1 && filteredSuggestions.length > 0 && (
        <Suggestions>
          <ul>
            {filteredSuggestions.map((suggestion) => {
              const index = suggestion.title
                .toLowerCase()
                .indexOf(inputValue.toLowerCase());
              const firstHalf = suggestion.title.substring(0, index);
              const secondHalf = suggestion.title.substring(
                index + inputValue.length
              );
              const category = categories[suggestion.categoryId];

              return (
                <li key={suggestion.id}>
                  <SuggestionItem
                    onClick={() => handleSelect(suggestion.title)}
                  >
                    <span>
                      {firstHalf}
                      {inputValue}
                      <Prediction>{secondHalf}</Prediction>
                    </span>
                    <Category>({category.name})</Category>
                  </SuggestionItem>
                </li>
              );
            })}
          </ul>
        </Suggestions>
      )}
    </React.Fragment>
  );
};

const Prediction = styled.span`
  font-weight: bold;
`;

const Category = styled.span`
  margin-left: 0.5rem;
  font-style: italic;
  font-size: small;
  color: purple;
  &:before {
    content: "in ";

    color: black;
  }
`;

const InputWrapper = styled.div`
  display: flex;
  align-items: center;
  flex-direction: row;
`;
const Suggestions = styled.div`
  width: 25em;
  margin-top: 0.5em;
  padding: 0;
  box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.2);
  background-color: white;
  border: 1px solid lightgray;
  border-radius: 3px;
  z-index: 1;
`;

const SuggestionItem = styled.div`
  padding: 0.4em;
  cursor: pointer;
  &:hover {
    background-color: lightgoldenrodyellow;
  }
`;

const Input = styled.input`
  padding: 0.4em;
  width: 25em;
  border: 1px solid lightgray;
  border-radius: 3px;
`;

const Button = styled.button`
  padding: 0.5em;
  width: 5em;
  margin-left: 0.7em;
  color: white;
  background-color: rgb(0, 0, 200);
  border-radius: 5px;
  border: none;
`;

export default Typeahead;
